sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
// 	var nowPlusOneDay = new GlideDateTime(rightNow.addDaysUTC(1));
// gs.info('RY: rightnow = ' + rightNow);
// gs.info('RY: nowPlusOneDay = ' + nowPlusOneDay.getDate());

	// Query the database for NeedIt Task records with Due date field values equal to
	// the current time or 24 hours after it.  Only return NeedIt Task records that do not 
	// have a State field value of Closed Complete.
	var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');
	dueSoonNITasks.addQuery('due_date','>=',rightNow);
	dueSoonNITasks.addQuery('due_date','<=',gs.hoursAgo(-24));
	dueSoonNITasks.addQuery('state','!=',3);

	dueSoonNITasks.query();
	// Write a log message for each NeedIt Task Record due within 24 hours
	while(dueSoonNITasks.next()){
		gs.info("NeedIt Task record due within 24 hours = " + dueSoonNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', dueSoonNITasks,dueSoonNITasks.number, gs.getUserName());
	}
